import subprocessimport osimport csvdef run_prodigal(input_file, outdir, cores=1, is_genome=False):    base_name = os.path.basename(input_file).split('.')[0]    protein_out = os.path.join(outdir, f"{base_name}_proteins.fa")    gene_out = os.path.join(outdir, f"{base_name}_genes.fna")    log_file = os.path.join(outdir, f"{base_name}_prodigal.log")    mapping_csv=None    prodigal_mode = "single" if is_genome else "meta"    cmd = [        "prodigal",        "-i", input_file,        "-a", protein_out,        "-d", gene_out,        "-p", prodigal_mode,    ]    os.makedirs(outdir, exist_ok=True)    with open(log_file, "w") as log:        subprocess.check_call(cmd, stdout=log, stderr=log)    return protein_out, gene_out, mapping_csv